function addBusinessDays(date, days) {
    let result = new Date(date);
    let count = 0;
    while (count < days) {
        result.setDate(result.getDate() + 1);
        // Check if the day is not a weekend (0 is Sunday, 6 is Saturday)
        if (result.getDay() !== 0 && result.getDay() !== 6) {
            count++;
        }
    }
    return result;
}

