part.onChange:
let productionMethods = $getGrid("productionMethods")

let productionMethodFiltered = productionMethods.filter((productionMethod) => {
    return productionMethod.part === rowData.part
}) 
console.log('prod filtered method: ',productionMethodFiltered)
$dgSetRowVals("quoteLines", rowData.rowKey, {
    sourceProductionMethod: productionMethodFiltered[0]?.rowKey
})

onRecordCreate:
if(!rowData.name) {
console.log('going in lines')
setTimeout(() => {
    console.log('data in here: ', rowData);
    const quoteLinesFiltered = $getGrid('quoteLines').filter((line) => line.quote === rowData.quote);

    const parentId = rowData.$quote$display;
    const lineIndex = quoteLinesFiltered.length; // Incrementing to get the next index
    const newId = `${parentId}-${String(lineIndex).padStart(2, '0')}`; // Formatted ID

    $dgSetRowVals('quoteLines', rowData.rowKey, {
        name: newId
    });
}, 1000);
}
